import React, {useState,useEffect} from "react";
import axios from "axios"
import { useHistory, useParams } from 'react-router-dom';
import RegistrationTile from './RegistrationTile'
import { useSnackbar } from 'notistack';
import dompurify from 'dompurify'


const EventDetails = (props) => {
  const { enqueueSnackbar, closeSnackbar } = useSnackbar();
  const [bomEvent, setbomEvent] = useState([]);
  const [registrations, setRegistrations] = useState([]);
  console.log(JSON.stringify(props));
  const params = useParams();
  const [contentLoaded, setContentLoaded] = useState(false);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(false);
  const eventid = params.eventid;

  const history = useHistory();


  useEffect(() => {
    const fetchData = async () => {
    try{
      setIsLoading(true);
      axios.all([
          axios.get('https://bomreactapi.azurewebsites.net/events/geteventsinfo?eventid=' + eventid),
          axios.get('https://bomreactapi.azurewebsites.net/events/getregistrations?eventid=' + eventid)
      ]).then(response=> {
          setbomEvent(response[0].data);
          setRegistrations(response[1].data);
          enqueueSnackbar('Event fetch success');
          setIsLoading(false);
          setContentLoaded(true);
      })

      }
        catch(error){
        setError(true);
        setContentLoaded(false);
        enqueueSnackbar('Event fetch failed');
      }        
    };

    if(!contentLoaded){

      fetchData();
      setContentLoaded(true);        
      setIsLoading(false);
    }
}, []);


  return (
<div id="">
    {/* this is the blue banner bar */}
    <div className="full-row light-blue">
        <div className="container-fluid">
          <div className="row text-white pt-3 mb-0 event-highlights">
            <div className="col-md-4 col pt-3 pb-2 right-divide">
              <h6>Location</h6>
              <p>{bomEvent.BP_Address}</p>
            </div>
            <div className="col-md-4 col pt-3 pb-2 right-divide">
              <h6>Dates</h6>
              <p>{bomEvent.BP_StartDate - bomEvent.BP_EndDate}</p>
            </div>
            <div className="col-md-4 col pt-3 pb-2 right-divide">
              <h6> Costs</h6>
              <p>Travel, Vehicle Rental and Some Food Costs</p>
            </div>
          </div>
        </div>
      </div>
      {/* End Blue Banner Bar*/}
      {/* White Banner Bar */}
      <div className="full-row white">
        <div className="container-fluid">
          <div className="row">
            <div className="col-md-12"> 
            { bomEvent.BP_Reserved ? 
            <div>
                <span className="reserved-label">Reserved</span><span className="reserved-copy">{bomEvent.BP_ReservedText}</span>
            </div>
            : 
            <div>

            </div> }

            </div>
          </div>
        </div>
      </div>
      {/* End White Banner Bar*/}
      {/* Event Details Desc*/}
      <div className="full-row white">
        <div className="container-fluid">
          <div className="row justify-content-between pt-4 pb-4">
            <div className="col-md-8 pb-3">
            {<div dangerouslySetInnerHTML={{ __html: dompurify.sanitize(bomEvent?.BP_Description) }} /> }
            </div>
            <div className="col-md-3 pb-3">
              <h6>Contact</h6>
              <p>Mark Abernathy<br/>
                <a href="mailto:mabernathy@ncbaptist.org">mabernathy@ncbaptist.org</a><br/>
                (919) 459-5607</p>
              <p>Melanie Crawford<br/>
                <a href="mailto:mcrawford@ncbaptist.org">mcrawford@ncbaptist.org</a></p>
            </div>
          </div>
        </div>
      </div>
      {/* End Event Details Desc*/}
    <div class="container-fluid">
      { registrations.map(reg =>
                    <RegistrationTile key={reg.RegistrationID} Registration={reg}/>
                )}
    </div>
  </div>
         ) 
}



export default EventDetails;
